
public class variablesUpdate {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Declaring & Initialization of variable in one line 
		
				int distanceAdam = 0 ,distanceLucy = 0;
				
				// Printing out initial values of these variables
				
				System.out.println("initial value" + distanceAdam);
				System.out.println("initial value" + distanceLucy);
				
				
				// updating these variables
				distanceAdam = distanceAdam + 20;
				
				// updating variable by overwriting 
				distanceLucy = 15;
				
				
				// printing out final updated values
				System.out.println("updated value" + distanceAdam);
				System.out.println("updated value" + distanceLucy);

	}

}
